mapper.insert:

	SELECT SEQ_HOBBY_NO.NEXTVAL FROM dual


INSERT INTO 
	tbl_hobby (hobby_no, hobby_writer, hobby_title, main_img,
		hobby_intro, hobby_video, l_cate_no, m_cate_no, 
		time_no, level_no, cost_no 
	)
VALUES (#{hobby_no}, #{hobby_writer}, #{hobby_title}, #{main_img},
	#{hobby_intro}, #{hobby_video, jdbcType=VARCHAR}, #{l_cate_no}, #{m_cate_no},
	#{time_no}, #{level_no}, #{cost_no}
)

mapper.select:
		SELECT * FROM tbl_make_step
 		WHERE hobby_no = #{hobby_no}
 		ORDER BY make_step_num ASC

mapper.update:
MERGE INTO tbl_hobby_material A USING (
SELECT #{item.hm_no} as hm_no #{item.hobby_no} as hobby_no, #{item.material_no} as material_no, #{item.material_detail} as material_detail, #{item.seq} as seq FROM DUAL
) B ON (A.HM_NO = B.HM_NO) WHEN MATCHED THEN UPDATE SET A.MATERIAL_NO = B.material_no, A.MATERIAL_DETAIL = B.material_detail, A.SEQ = B.seq WHERE A.HOBBY_NO = B.hobby_no WHEN NOT MATCHED THEN INSERT ( HM_NO, HOBBY_NO, MATERIAL_NO, MATERIAL_DETAIL, SEQ ) VALUES ( seq_hm_no.nextval, B.hobby_no, B.material_no, B.material_detail, B.seq )
